// ==UserScript==
// @name           Warchaos Icon Replacer
// @namespace      https://github.com/shazgorn/warchaos_userjs
// @description    Replace default recipe icon(1914.gif). Add art info
// @match          http://warchaos.ru/*
// @version        1.12
// @downloadURL    https://raw.github.com/shazgorn/warchaos_userjs/master/wc_icon_replacer.user.js
// ==/UserScript==

(function () {
	// return;
	function insertAfter(el1,el2) {
		if (!el2.parentNode)return;
		if (el2.nextSibling)
			el2.parentNode.insertBefore(el1,el2.nextSibling);
		else
			el2.parentNode.appendChild(el1);
	}

	function replaceIcons() {
	//icons by ~DjiN~
	var icons = [
// artefacts
["Рецепт: Булава", "Recipe: Mace", "data:image/gif;base64,"],
["Рецепт: Деревянный щит", "Recipe: Wooden shield", "data:image/gif;base64,"],
["Рецепт: Дубинка орков", "Recipe: Orc's club", "data:image/gif;base64,"],
["Рецепт: Золотая броня", "Recipe: Golden armor", "data:image/gif;base64,"],
["Рецепт: Зубы орка", "Recipe: Orc teeth", "data:image/gif;base64,"],
["Рецепт: Кожаная броня", "Recipe: Leather armor", "data:image/gif;base64,"],
["Рецепт: Короткий меч", "Recipe: Short sword", "data:image/gif;base64,"],
["Рецепт: Меч-самосек", "Recipe: Claymore", "data:image/gif;base64,"],
["Рецепт: Молот силы", "Recipe: Hammer", "data:image/gif;base64,"],
["Рецепт: Подзорная труба", "Recipe: Telescope", "data:image/gif;base64,"],
["Рецепт: Посох патриарха", "Recipe: Patriarch staff", "data:image/gif;base64,"],
["Рецепт: Разрывчатый лук", "Recipe: Composite Bow", "data:image/gif;base64,"],
["Рецепт: Сапоги", "Recipe: Boots", "data:image/gif;base64,"],
["Рецепт: Спиритический шлем", "Recipe: Spirit helmet", "data:image/gif;base64,"],
// black magic
["Рецепт: Вихрь", "Recipe: Tornado", "data:image/gif;base64,"],
["Рецепт: Забывчивость", "Recipe: Forget", "data:image/gif;base64,"],
["Рецепт: Огненная стена", "Recipe: Firewall", "data:image/gif;base64,"],
["Рецепт: Ослепление", "Recipe: Blind", "data:image/gif;base64,"],
["Рецепт: Паутина", "Recipe: Spider web", "data:image/gif;base64,"],
["Рецепт: Пентаграмма", "Recipe: Pentagram", "data:image/gif;base64,"],
["Рецепт: Проклятие", "Recipe: Curse", "data:image/gif;base64,"],
["Рецепт: Слабость", "Recipe: Enfeeble", "data:image/gif;base64,"],
// white magic
["Рецепт: Благословение", "Recipe: Bless", "data:image/gif;base64,"],
["Рецепт: Горгулья", "data:image/gif;base64,"],
["Рецепт: Дождь", "Recipe: Rain", "data:image/gif;base64,"],
["Рецепт: Куст терновника", "Recipe: Bush", "data:image/gif;base64,"],
["Рецепт: Маскировка", "Recipe: Fade", "data:image/gif;base64,"],
["Рецепт: Отражение", "Recipe: Clone", "data:image/gif;base64,"],
["Рецепт: Радуга", "Recipe: Rainbow", "data:image/gif;base64,"],
["Рецепт: Рассеивание", "Recipe: Dispel", "data:image/gif;base64,"],
["Рецепт: Суперсенсинг", "Recipe: Supersensing", "data:image/gif;base64,"]
];
		
		var re = /it\/(\d+)\.gif/i,  //pic
			art = /(I*V*)\** *((?:SL|FC|R|r\d+| )*)* *(?:\$Осталось: )*(\d+)/, //art info
			re3 = /x\d+/, //crafts
			parent, text, el,
			imgs = document.images;
		var romeToArab = {
			I: 1,
			II: 2,
			III: 3,
			IV: 4,
			V: 5
		}
		
		for (var i = 0; i < imgs.length; i++) {
			if (!imgs[i].hasAttribute('sck') && imgs[i].hasAttribute("src") ) {
				var m = imgs[i].getAttribute('src').match(re);
				var item = m ? parseInt(m[1], 10) : 0;
				if (item == 1914)
					for (var j = 0; j < icons.length; j++) {
						if (imgs[i].hasAttribute("tooltip")) {
							for (var k = 0; k < icons[j].length - 1; k++) {
								if (imgs[i].getAttribute('tooltip').search(icons[j][k]) != -1) {
									parent = imgs[i].parentNode;
									if (parent.nodeName == "BUTTON") {
										parent.setAttribute('style', "max-height:40px;");
									}
									imgs[i].setAttribute('style', "position:relative;left:-2px;top:0px;");//height:34px;width:34px;
									if (imgs[i].getAttribute("height") == "40") {
										imgs[i].setAttribute("height", 34);
										imgs[i].setAttribute("width", 34);
									}
									imgs[i].setAttribute('src', icons[j][icons[j].length-1]);
									m = re3.exec(imgs[i].getAttribute('tooltip'));
									text = m ? m[0] : '?';
									el = document.createElement('span');
									el.setAttribute('style',"position:relative;top:-3px;left:0px;color:yellow;");
									el.appendChild(document.createElement('br'));
									el.appendChild(document.createTextNode(text));
									insertAfter(el,imgs[i]);
								}
							}
						}
					}
				else if (item >= 604 && item <= 1764) {
					parent = imgs[i].parentNode;
					if (parent.nodeName == "BUTTON") {
						parent.setAttribute('style', "max-height:40px;");
					}
					if (imgs[i].hasAttribute("tooltip") && parent.childNodes.length == 1 || (parent.childNodes.length == 3 && parent.childNodes[2].nodeName == 'INPUT') || (imgs[i].parentNode.hasAttribute("class") && imgs[i].parentNode.getAttribute("class") == "tlnx" && imgs[i].parentNode.childNodes.length == 1) || (imgs[i].parentNode.hasAttribute("class") && imgs[i].parentNode.getAttribute("class") == "tlnx" && imgs[i].parentNode.childNodes.length == 2 && 
							imgs[i].nextSibling.getAttribute("src") == "q.gif")
						) {
						m = art.exec(imgs[i].getAttribute('tooltip'));
						var lvl = document.createElement('span');
						console.log(m);
						var style = 'color:yellow; position:relative; width:45px; white-space:nowrap;';
						lvl.setAttribute('style', style + 'top:-40px; left:-15px; font-size: 12px; font-weight: bold;');
						lvl.innerHTML = "<br>" + romeToArab[m[1]] + "<br>";
						insertAfter(lvl, imgs[i]);
						el = document.createElement('span');
						if (typeof m[2] === "undefined") {
							el.innerHTML = m[3];
							el.setAttribute('style', style + 'top:-27px;left:12; font-size: 10px;');
						} else {
							el.innerHTML = m[2] + (m[2].length == 2 ? "&nbsp;&nbsp;" : "") + m[3];
							el.setAttribute('style', style + 'top:-27px;left:0; font-size: 10px;');
						}
						insertAfter(el, lvl);
						

					}
				}
			}
		}//for
	}

	(function(f) {
		addEventListener('click', function () {
			setTimeout(f, 0);
		}, false);
		var wc_ifr = document.getElementById("ifr");
		if (wc_ifr)
			wc_ifr.addEventListener("load", function () {
				setTimeout(f, 0);
			}, false);
		f();
	})(replaceIcons);
})();

